package com.brijesh.ProductService.controller;import com.brijesh.ProductService.model.ProductRequest;import com.brijesh.ProductService.model.ProductResponse;import com.brijesh.ProductService.service.ProductService;import lombok.RequiredArgsConstructor;import org.apache.http.protocol.HTTP;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/products")public class ProductController {    @Autowired    private ProductService productService;    @PostMapping("/")    public ResponseEntity<Long> addProduct(@RequestBody ProductRequest productRequest){        long productId = productService.createProduct(productRequest);        return new ResponseEntity<>(productId, HttpStatus.CREATED);    }    @GetMapping("/{id}")    public ResponseEntity<ProductResponse> getProductById(@PathVariable("id") long productId) {        ProductResponse productResponse = productService.getProductById(productId);        return new ResponseEntity<>(productResponse, HttpStatus.FOUND);    }    @GetMapping("/get/{id}")    public ProductResponse getProductByIdClient(@PathVariable("id") long productId) {        return productService.getProductById(productId);    }    @PutMapping("/reduce-quantity/{id}")    public ResponseEntity<Void> reduceQuantity(@PathVariable long id, @RequestParam long quantity){        productService.reduceQuantity(id, quantity);        return new ResponseEntity<>(HttpStatus.OK);    }}