package com.brijesh.ProductService.controller;import com.brijesh.ProductService.exception.ProductServiceException;import com.brijesh.ProductService.model.ErrorResponse;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.servlet.mvc.method.annotation.ResponseEntityExceptionHandler;@ControllerAdvicepublic class ProductServiceExceptionHandler extends ResponseEntityExceptionHandler {    @ExceptionHandler(ProductServiceException.class)    public ResponseEntity<ErrorResponse> productNotFoundException(ProductServiceException exception){        return new ResponseEntity<>(ErrorResponse.builder().errorMessage(exception.getMessage()).errorCode(exception.getErrorCode()).build(), HttpStatus.NOT_FOUND);    }}