package com.brijesh.orderService.controller;import com.brijesh.orderService.model.OrderRequest;import com.brijesh.orderService.model.OrderResponse;import com.brijesh.orderService.service.OrderService;import lombok.extern.log4j.Log4j2;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/orders")@Log4j2public class OrderController {    @Autowired    private OrderService orderService;    @PostMapping("/place-order")    public ResponseEntity<OrderResponse> createOrder(@RequestBody OrderRequest orderRequest){        OrderResponse orderResponse = orderService.placeOrder(orderRequest);        log.info("Order created: {}", orderResponse);        return new ResponseEntity<>(orderResponse, HttpStatus.CREATED);    }}