package com.brijesh.orderService.exception;import com.brijesh.orderService.external.response.ErrorResponse;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.servlet.mvc.method.annotation.ResponseEntityExceptionHandler;@ControllerAdvicepublic class OrderServiceExceptionHandler extends ResponseEntityExceptionHandler {    @ExceptionHandler(CustomException.class)    public ResponseEntity<ErrorResponse> productNotFoundException(CustomException exception){        return new ResponseEntity<>(ErrorResponse.builder().                errorMessage(exception.getMessage()).                errorCode(exception.getErrorCode()).build(),                HttpStatus.NOT_FOUND);    }}