package com.brijesh.orderService.external.client;import com.brijesh.orderService.external.response.ProductResponse;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestParam;@FeignClient(name = "PRODUCT-SERVICE", url = "http://localhost:8080/products")public interface ProductServiceClient {    @PutMapping("/reduce-quantity/{id}")    ResponseEntity<Void> reduceQuantity(@PathVariable long id, @RequestParam long quantity);    @GetMapping("/get/{id}")    public ProductResponse getProductByIdClient(@PathVariable("id") long productId);}